import java.util.List;

import okhttp3.MultipartBody;
import retrofit2.Call;
import retrofit2.http.Body;
import retrofit2.http.DELETE;
import retrofit2.http.GET;
import retrofit2.http.Header;
import retrofit2.http.Multipart;
import retrofit2.http.POST;
import retrofit2.http.PUT;
import retrofit2.http.Part;

public interface ApiService {

    // Endpoints de autenticación
    @POST("/auth/signup")
    Call<SignupResponse> signup(@Body SignupRequest request);

    @POST("/auth/login")
    Call<LoginResponse> login(@Body LoginRequest request);

    // Endpoint de Google Sign-In (único para login y signup)
    @POST("/auth/google")
    Call<LoginResponse> googleSignIn(@Body GoogleSignInRequest request);

    // Endpoints de perfil
    @POST("/auth/complete-profile")
    Call<ProfileResponse> createProfile(
            @Header("Authorization") String token,
            @Body ProfileRequest request
    );

    @GET("/profile/me")
    Call<GetProfileResponse> getProfile(
            @Header("Authorization") String token
    );

    @GET("/profile/{userId}")
    Call<GetProfileResponse> getUserProfile(
            @Header("Authorization") String token,
            @retrofit2.http.Path("userId") int userId
    );

    @PUT("/profile/me")
    Call<ProfileResponse> updateProfile(
            @Header("Authorization") String token,
            @Body UpdateProfileRequest request
    );

    // Endpoints de lookup (deportes y ubicaciones)
    @GET("/lookup/sports")
    Call<List<Sport>> getSports();

    @GET("/lookup/locations")
    Call<List<Location>> getLocations();

    // Endpoints de foto de perfil
    @Multipart
    @POST("/profile-photo/upload")
    Call<UploadPhotoResponse> uploadProfilePhoto(
            @Header("Authorization") String token,
            @Part MultipartBody.Part photo
    );

    @DELETE("/profile-photo/delete")
    Call<DeletePhotoResponse> deleteProfilePhoto(
            @Header("Authorization") String token
    );

    // Endpoints de publicaciones
    @POST("/posts")
    Call<CreatePostResponse> createPost(
            @Header("Authorization") String token,
            @Body CreatePostRequest request
    );

    @GET("/posts")
    Call<List<Post>> getAllPosts();

    @GET("/posts/my-posts")
    Call<MyPostsResponse> getMyPosts(
            @Header("Authorization") String token
    );

    @DELETE("/posts/{id}")
    Call<Void> deletePost(
            @Header("Authorization") String token,
            @retrofit2.http.Path("id") int postId
    );

    // Endpoints de swipe y matches
    @GET("/swipe/discover")
    Call<DiscoverResponse> getDiscoverUsers(
            @Header("Authorization") String token,
            @retrofit2.http.Query("profile_type_filter") String profileTypeFilter,
            @retrofit2.http.Query("limit") Integer limit
    );

    @POST("/swipe")
    Call<SwipeResponse> swipe(
            @Header("Authorization") String token,
            @Body SwipeRequest request
    );

    @GET("/swipe/matches")
    Call<MatchesResponse> getMatches(
            @Header("Authorization") String token
    );

    // Endpoints de suscripción
    @GET("/subscriptions/plans")
    Call<List<SubscriptionPlan>> getSubscriptionPlans(
            @Header("Authorization") String token
    );

    @POST("/subscriptions/create-checkout-session")
    Call<CheckoutSessionResponse> createCheckoutSession(
            @Header("Authorization") String token,
            @Body CreateCheckoutRequest request
    );

    @GET("/subscriptions/status")
    Call<SubscriptionStatus> getSubscriptionStatus(
            @Header("Authorization") String token
    );

    @POST("/subscriptions/cancel")
    Call<Void> cancelSubscription(
            @Header("Authorization") String token
    );

    // Endpoints de venues
    @GET("/venues")
    Call<VenuesResponse> getVenues(
            @Header("Authorization") String token
    );

    @GET("/venues/{id}")
    Call<VenuesResponse> getVenueById(
            @Header("Authorization") String token,
            @retrofit2.http.Path("id") int venueId
    );
}

package com.example.sportconnection.repository;

import com.example.sportconnection.model.LoginRequest;
import com.example.sportconnection.model.LoginResponse;
import com.example.sportconnection.model.SignupRequest;
import com.example.sportconnection.model.ProfileRequest;
import com.example.sportconnection.model.ProfileResponse;
import com.example.sportconnection.model.GetProfileResponse;
import com.example.sportconnection.model.UpdateProfileRequest;
import com.example.sportconnection.model.SignupResponse;
import com.example.sportconnection.model.GoogleSignInRequest;
import com.example.sportconnection.model.UploadPhotoResponse;
import com.example.sportconnection.model.DeletePhotoResponse;
import com.example.sportconnection.network.ApiClient;
import com.example.sportconnection.network.ApiService;

import java.io.File;

import okhttp3.MediaType;
import okhttp3.MultipartBody;
import okhttp3.RequestBody;
import retrofit2.Call;
import retrofit2.Callback;

public class AuthRepository {
    private final ApiService apiService;

    public AuthRepository() {
        apiService = ApiClient.getClient().create(ApiService.class);
    }

    // Registro de usuario
    public void signup(String email, String password, Callback<SignupResponse> callback) {
        SignupRequest request = new SignupRequest(email, password);
        Call<SignupResponse> call = apiService.signup(request);
        call.enqueue(callback);
    }

    // Login de usuario
    public void login(String email, String password, Callback<LoginResponse> callback) {
        LoginRequest request = new LoginRequest(email, password);
        Call<LoginResponse> call = apiService.login(request);
        call.enqueue(callback);
    }

    // Google Sign-In (unificado para login y signup)
    public void googleSignIn(String idToken, Callback<LoginResponse> callback) {
        GoogleSignInRequest request = new GoogleSignInRequest(idToken);
        Call<LoginResponse> call = apiService.googleSignIn(request);
        call.enqueue(callback);
    }

    // Crear perfil
    public void createProfile(String token, ProfileRequest request, Callback<ProfileResponse> callback) {
        String authHeader = "Bearer " + token;
        Call<ProfileResponse> call = apiService.createProfile(authHeader, request);
        call.enqueue(callback);
    }

    // Obtener perfil
    public void getProfile(String token, Callback<GetProfileResponse> callback) {
        String authHeader = "Bearer " + token;
        Call<GetProfileResponse> call = apiService.getProfile(authHeader);
        call.enqueue(callback);
    }

    // Actualizar perfil
    public void updateProfile(String token, UpdateProfileRequest request, Callback<ProfileResponse> callback) {
        String authHeader = "Bearer " + token;
        Call<ProfileResponse> call = apiService.updateProfile(authHeader, request);
        call.enqueue(callback);
    }

    // Subir foto de perfil
    public void uploadProfilePhoto(String token, File photoFile, Callback<UploadPhotoResponse> callback) {
        String authHeader = "Bearer " + token;

        // Crear RequestBody para el archivo
        RequestBody requestFile = RequestBody.create(MediaType.parse("image/*"), photoFile);

        // Crear MultipartBody.Part para enviar el archivo
        MultipartBody.Part photoPart = MultipartBody.Part.createFormData("photo", photoFile.getName(), requestFile);

        Call<UploadPhotoResponse> call = apiService.uploadProfilePhoto(authHeader, photoPart);
        call.enqueue(callback);
    }

    // Eliminar foto de perfil
    public void deleteProfilePhoto(String token, Callback<DeletePhotoResponse> callback) {
        String authHeader = "Bearer " + token;
        Call<DeletePhotoResponse> call = apiService.deleteProfilePhoto(authHeader);
        call.enqueue(callback);
    }
}

